scripts.postResponse:
// Test the structure of the successful response
rq.test("Response body structure is valid", function () {
    var responseData = rq.response.json();
    rq.expect(responseData).to.have.property('account');
    rq.expect(responseData.account).to.be.an('object').that.has.property('id');
});

var response = rq.response.text()

// Test the response body structure
rq.test("Response body to be empty", function () {
    rq.expect(response).to.be.empty;
});

// Test the response body structure and values
rq.test("Response body structure is valid", function () {
    var responseJSON = rq.response.json();
    rq.expect(responseJSON).to.have.property('account').that.is.an('object');
    rq.expect(responseJSON.account).to.have.property('id').that.is.a('number');
    rq.expect(responseJSON.account).to.have.property('owner').that.is.a('string');
    rq.expect(responseJSON.account).to.have.property('balance').that.is.a('number');
    rq.expect(responseJSON.account).to.have.property('currency').that.is.a('string');
});

// Test to check the structure of the successful response
rq.test("Response body structure is valid", function () {
    var responseJSON = rq.response.json();
    rq.expect(responseJSON).to.have.property('accounts').that.is.an('array');
    if (responseJSON.accounts.length > 0) {
        responseJSON.accounts.forEach(function(account) {
            rq.expect(account).to.have.property('id');
            rq.expect(account).to.have.property('owner');
            rq.expect(account).to.have.property('createdAt');
            rq.expect(account).to.have.property('balance');
            rq.expect(account).to.have.property('currency');
        });
    }
});

// Test to check the structure of the successful response
rq.test("Successful response has the correct structure", function () {
    var responseData = rq.response.json();

    // Check if the response contains the account object
    rq.expect(responseData).to.have.property('account');
    rq.expect(responseData.account).to.be.an('object');

    // Check the properties of the account object
    rq.expect(responseData.account).to.have.property('id');
    rq.expect(responseData.account).to.have.property('owner');
    rq.expect(responseData.account).to.have.property('balance');
    rq.expect(responseData.account).to.have.property('currency');
    rq.expect(responseData.account).to.have.property('createdAt');
});

var responseJSON = rq.response.json();

// Test the response body structure and values
rq.test("Response body structure is valid", function () {
    rq.expect(responseJSON).to.be.an('object').that.has.property('success', true);
    rq.expect(responseJSON).to.have.property('transaction').that.is.an('object').and.has.property('id');
});

// Test the response body structure and values
rq.test("Response body structure is valid", function () {
    var responseJSON = rq.response.json();
    rq.expect(responseJSON).has.property('transaction');
    rq.expect(responseJSON.transaction).to.be.an('object').that.has.all.keys('id', 'createdAt', 'fromAccountId', 'toAccountId', 'currency', 'amount');
});

var responseData = rq.response.json();

// Test to check if the response is an object with a transactions property
rq.test("Response is an object with transactions property", function () {
    rq.expect(responseData).to.be.an('object').that.has.property('transactions');
});

// Test to check the structure and data types of the transactions array
rq.test("Transactions array structure and data types are valid", function () {
    responseData.transactions.forEach(function(transaction) {
        rq.expect(transaction).to.be.an('object').that.has.property('id').to.be.a('number');
        rq.expect(transaction).to.have.property('createdAt').to.be.a('string');
        rq.expect(transaction).to.have.property('fromAccountId').to.be.a('number');
        rq.expect(transaction).to.have.property('toAccountId').to.be.a('number');
        rq.expect(transaction).to.have.property('currency').to.be.a('string');
        rq.expect(transaction).to.have.property('amount').to.be.a('number');
    });
});
